// Individual trip card component
import React from "react";
import { useAuth } from "../../context/AuthContext";
import { bookTrip } from "../../services/api";

const TripCard = ({ trip, onTripUpdate, onDelete }) => {
  const { user } = useAuth();

  const handleBooking = async () => {
    if (!user) {
      alert("Please login to book a trip");
      return;
    }

    try {
      const response = await bookTrip(trip._id);
      alert("Trip booked successfully!");
      if (onTripUpdate) {
        onTripUpdate(response.data.trip);
      }
    } catch (error) {
      alert(error.response?.data?.message || "Booking failed");
    }
  };

  const handleDelete = async () => {
    if (window.confirm("Are you sure you want to delete this trip?")) {
      if (onDelete) {
        onDelete(trip._id);
      }
    }
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString();
  };

  const isFullyBooked = trip.currentBookings >= trip.maxPeople;

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow">
      <img
        src={trip.image}
        alt={trip.title}
        className="w-full h-48 object-cover"
      />

      <div className="p-6">
        <h3 className="text-xl font-bold text-gray-900 mb-2">{trip.title}</h3>
        <p className="text-gray-600 mb-2">{trip.destination}</p>
        <p className="text-gray-500 text-sm mb-4 line-clamp-3">
          {trip.description}
        </p>

        <div className="grid grid-cols-2 gap-4 mb-4 text-sm">
          <div>
            <span className="font-medium">Duration:</span> {trip.duration} days
          </div>
          <div>
            <span className="font-medium">Price:</span> ${trip.price}
          </div>
          <div>
            <span className="font-medium">Start Date:</span>{" "}
            {formatDate(trip.startDate)}
          </div>
          <div>
            <span className="font-medium">End Date:</span>{" "}
            {formatDate(trip.endDate)}
          </div>
        </div>

        <div className="flex justify-between items-center mb-4">
          <span className="text-sm text-gray-600">
            {trip.currentBookings}/{trip.maxPeople} booked
          </span>
          <div className="w-full bg-gray-200 rounded-full h-2 mx-4">
            <div
              className="bg-blue-600 h-2 rounded-full"
              style={{
                width: `${(trip.currentBookings / trip.maxPeople) * 100}%`,
              }}
            ></div>
          </div>
        </div>

        <div className="flex justify-between items-center">
          <button
            onClick={handleBooking}
            disabled={isFullyBooked || !user}
            className={`px-4 py-2 rounded font-medium ${
              isFullyBooked || !user
                ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                : "bg-blue-600 text-white hover:bg-blue-700"
            }`}
          >
            {isFullyBooked ? "Fully Booked" : "Book Now"}
          </button>

          {user && user.isAdmin && (
            <button
              onClick={handleDelete}
              className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
            >
              Delete
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default TripCard;
