// Trip list component - displays all trips
import React, { useState, useEffect } from "react";
import { getTrips, deleteTrip } from "../../services/api";
import TripCard from "./TripCard";

const TripList = () => {
  const [trips, setTrips] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchTrips();
  }, []);

  const fetchTrips = async () => {
    try {
      const response = await getTrips();
      setTrips(response.data);
    } catch (error) {
      setError("Failed to fetch trips");
    } finally {
      setLoading(false);
    }
  };

  const handleTripUpdate = (updatedTrip) => {
    setTrips(
      trips.map((trip) => (trip._id === updatedTrip._id ? updatedTrip : trip))
    );
  };

  const handleTripDelete = async (tripId) => {
    try {
      await deleteTrip(tripId);
      setTrips(trips.filter((trip) => trip._id !== tripId));
      alert("Trip deleted successfully");
    } catch (error) {
      alert("Failed to delete trip");
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-64">
        <div className="text-xl">Loading trips...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center text-red-600 min-h-64 flex items-center justify-center">
        {error}
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h2 className="text-3xl font-bold text-center mb-8">Available Trips</h2>

      {trips.length === 0 ? (
        <div className="text-center text-gray-600">
          <p className="text-xl">No trips available at the moment.</p>
          <p className="mt-2">Check back later for new destinations!</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {trips.map((trip) => (
            <TripCard
              key={trip._id}
              trip={trip}
              onTripUpdate={handleTripUpdate}
              onDelete={handleTripDelete}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default TripList;
