// Frontend - AddTrip.js with file upload
import React, { useState } from "react";

const AddTrip = () => {
  const [tripData, setTripData] = useState({
    title: "",
    description: "",
    price: "",
  });

  const [selectedFile, setSelectedFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file && file.type.startsWith("image/")) {
      setSelectedFile(file);

      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("title", tripData.title);
    formData.append("description", tripData.description);
    formData.append("price", tripData.price);

    if (selectedFile) {
      formData.append("image", selectedFile);
    }

    try {
      const response = await fetch("/api/trips", {
        method: "POST",
        body: formData, // Don't set Content-Type header when using FormData
      });

      if (response.ok) {
        const result = await response.json();
        console.log("Trip created successfully:", result);
        // Reset form
        setTripData({ title: "", description: "", price: "" });
        setSelectedFile(null);
        setImagePreview(null);
      }
    } catch (error) {
      console.error("Error creating trip:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="trip-form">
      <div>
        <label>Trip Title:</label>
        <input
          type="text"
          value={tripData.title}
          onChange={(e) => setTripData({ ...tripData, title: e.target.value })}
          required
        />
      </div>

      <div>
        <label>Description:</label>
        <textarea
          value={tripData.description}
          onChange={(e) =>
            setTripData({ ...tripData, description: e.target.value })
          }
          required
        />
      </div>

      <div>
        <label>Price:</label>
        <input
          type="number"
          value={tripData.price}
          onChange={(e) => setTripData({ ...tripData, price: e.target.value })}
          required
        />
      </div>

      {/* File Upload */}
      <div>
        <label>Upload Trip Image:</label>
        <input
          type="file"
          accept="image/*"
          onChange={handleFileSelect}
          required
        />
      </div>

      {/* Image Preview */}
      {imagePreview && (
        <div className="image-preview">
          <label>Image Preview:</label>
          <img
            src={imagePreview}
            alt="Trip preview"
            style={{ width: "200px", height: "120px", objectFit: "cover" }}
          />
        </div>
      )}

      <button type="submit">Create Trip</button>
    </form>
  );
};

export default AddTrip;
