import React, { useState } from "react";

const AddTrip = () => {
  const [tripData, setTripData] = useState({
    title: "",
    description: "",
    price: "",
    duration: "",
    location: "",
  });

  const [selectedFile, setSelectedFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTripData({
      ...tripData,
      [name]: value,
    });
    // Clear any previous error when user starts typing
    if (error) setError("");
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    setError(""); // Clear any previous errors

    if (file) {
      // Validate file type
      if (!file.type.startsWith("image/")) {
        setError("Please select a valid image file (JPG, PNG, GIF, etc.)");
        return;
      }

      // Validate file size (5MB limit)
      if (file.size > 5 * 1024 * 1024) {
        setError("Image size should be less than 5MB");
        return;
      }

      setSelectedFile(file);

      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
    } else {
      setSelectedFile(null);
      setImagePreview(null);
    }
  };

  const resetForm = () => {
    setTripData({
      title: "",
      description: "",
      price: "",
      duration: "",
      location: "",
    });
    setSelectedFile(null);
    setImagePreview(null);
    setError("");
    setSuccess("");

    // Reset file input
    const fileInput = document.querySelector('input[type="file"]');
    if (fileInput) fileInput.value = "";
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError("");
    setSuccess("");

    // Validation
    if (!selectedFile) {
      setError("Please select an image for the trip");
      setIsLoading(false);
      return;
    }

    const formData = new FormData();
    formData.append("title", tripData.title.trim());
    formData.append("description", tripData.description.trim());
    formData.append("price", tripData.price);
    formData.append("duration", tripData.duration.trim());
    formData.append("location", tripData.location.trim());
    formData.append("image", selectedFile);

    try {
      const response = await fetch("/api/trips", {
        method: "POST",
        body: formData,
      });

      const result = await response.json();

      if (response.ok) {
        setSuccess("Trip created successfully!");
        resetForm();

        // Optional: Redirect after success
        // setTimeout(() => {
        //   window.location.href = '/admin/trips';
        // }, 2000);
      } else {
        setError(result.message || "Failed to create trip");
      }
    } catch (error) {
      console.error("Error creating trip:", error);
      setError("Network error. Please check your connection and try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-xl shadow-lg">
      <div className="text-center mb-8 pb-6 border-b-2 border-gray-100">
        <h2 className="text-3xl font-bold text-gray-800 mb-3">Add New Trip</h2>
        <p className="text-gray-600 text-lg">
          Create a new travel package for customers
        </p>
      </div>

      {/* Success Message */}
      {success && (
        <div className="flex items-center gap-3 p-4 mb-6 bg-green-50 border border-green-200 text-green-800 rounded-lg">
          <span className="text-xl">✓</span>
          <span className="font-medium">{success}</span>
        </div>
      )}

      {/* Error Message */}
      {error && (
        <div className="flex items-center gap-3 p-4 mb-6 bg-red-50 border border-red-200 text-red-800 rounded-lg">
          <span className="text-xl">⚠</span>
          <span className="font-medium">{error}</span>
        </div>
      )}

      <form
        onSubmit={handleSubmit}
        className={`space-y-6 ${
          isLoading ? "opacity-70 pointer-events-none" : ""
        }`}
      >
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div className="space-y-2">
            <label
              htmlFor="title"
              className="block text-sm font-semibold text-gray-700"
            >
              Trip Title *
            </label>
            <input
              type="text"
              id="title"
              name="title"
              value={tripData.title}
              onChange={handleInputChange}
              placeholder="e.g., 7 Days in Paris"
              className="w-full px-4 py-3 border-2 border-gray-200 rounded-lg focus:border-blue-500 focus:ring-2 focus:ring-blue-200 focus:outline-none transition-all duration-200 hover:border-blue-400"
              required
            />
          </div>

          <div className="space-y-2">
            <label
              htmlFor="location"
              className="block text-sm font-semibold text-gray-700"
            >
              Location *
            </label>
            <input
              type="text"
              id="location"
              name="location"
              value={tripData.location}
              onChange={handleInputChange}
              placeholder="e.g., Paris, France"
              className="w-full px-4 py-3 border-2 border-gray-200 rounded-lg focus:border-blue-500 focus:ring-2 focus:ring-blue-200 focus:outline-none transition-all duration-200 hover:border-blue-400"
              required
            />
          </div>
        </div>

        <div className="space-y-2">
          <label
            htmlFor="description"
            className="block text-sm font-semibold text-gray-700"
          >
            Description *
          </label>
          <textarea
            id="description"
            name="description"
            value={tripData.description}
            onChange={handleInputChange}
            placeholder="Describe the trip highlights, activities, and what makes it special..."
            rows="4"
            className="w-full px-4 py-3 border-2 border-gray-200 rounded-lg focus:border-blue-500 focus:ring-2 focus:ring-blue-200 focus:outline-none transition-all duration-200 hover:border-blue-400 resize-none"
            maxLength="500"
            required
          />
          <div className="text-right text-xs text-gray-500">
            {tripData.description.length}/500 characters
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div className="space-y-2">
            <label
              htmlFor="price"
              className="block text-sm font-semibold text-gray-700"
            >
              Price (USD) *
            </label>
            <input
              type="number"
              id="price"
              name="price"
              value={tripData.price}
              onChange={handleInputChange}
              placeholder="999"
              min="0"
              step="0.01"
              className="w-full px-4 py-3 border-2 border-gray-200 rounded-lg focus:border-blue-500 focus:ring-2 focus:ring-blue-200 focus:outline-none transition-all duration-200 hover:border-blue-400"
              required
            />
          </div>

          <div className="space-y-2">
            <label
              htmlFor="duration"
              className="block text-sm font-semibold text-gray-700"
            >
              Duration *
            </label>
            <input
              type="text"
              id="duration"
              name="duration"
              value={tripData.duration}
              onChange={handleInputChange}
              placeholder="e.g., 7 days, 2 weeks"
              className="w-full px-4 py-3 border-2 border-gray-200 rounded-lg focus:border-blue-500 focus:ring-2 focus:ring-blue-200 focus:outline-none transition-all duration-200 hover:border-blue-400"
              required
            />
          </div>
        </div>

        {/* File Upload Section */}
        <div className="space-y-2">
          <label
            htmlFor="image"
            className="block text-sm font-semibold text-gray-700"
          >
            Trip Image *
          </label>
          <div className="relative border-2 border-dashed border-gray-300 rounded-xl p-8 text-center bg-gray-50 hover:border-blue-400 hover:bg-blue-50 transition-all duration-200 cursor-pointer">
            <input
              type="file"
              id="image"
              accept="image/*"
              onChange={handleFileSelect}
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
              required
            />
            <div className="pointer-events-none">
              <span className="text-4xl mb-4 block">📷</span>
              <p className="text-gray-600 font-medium mb-2">
                {selectedFile
                  ? selectedFile.name
                  : "Click to upload an image or drag and drop"}
              </p>
              <small className="text-gray-500">PNG, JPG, GIF up to 5MB</small>
            </div>
          </div>
        </div>

        {/* Image Preview */}
        {imagePreview && (
          <div className="space-y-4">
            <label className="block text-sm font-semibold text-gray-700">
              Image Preview:
            </label>
            <div className="relative inline-block bg-gray-100 p-3 rounded-xl shadow-md">
              <img
                src={imagePreview}
                alt="Trip preview"
                className="w-64 h-40 object-cover rounded-lg"
              />
              <button
                type="button"
                className="absolute -top-2 -right-2 bg-red-500 hover:bg-red-600 text-white rounded-full w-8 h-8 flex items-center justify-center text-sm font-bold transition-colors duration-200"
                onClick={() => {
                  setSelectedFile(null);
                  setImagePreview(null);
                  document.querySelector('input[type="file"]').value = "";
                }}
              >
                ✕
              </button>
            </div>
          </div>
        )}

        <div className="flex flex-col sm:flex-row gap-4 justify-end pt-6 border-t border-gray-200">
          <button
            type="button"
            className="px-6 py-3 bg-gray-500 hover:bg-gray-600 text-white font-semibold rounded-lg transition-all duration-200 hover:-translate-y-0.5 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
            onClick={resetForm}
            disabled={isLoading}
          >
            Reset Form
          </button>

          <button
            type="submit"
            className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg transition-all duration-200 hover:-translate-y-0.5 hover:shadow-lg disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none flex items-center justify-center gap-2 min-w-[140px]"
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                Creating...
              </>
            ) : (
              "Create Trip"
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddTrip;
