import React, { useState } from "react";
import "./AddTrip.css"; // Make sure to create this CSS file

const AddTrip = () => {
  const [tripData, setTripData] = useState({
    title: "",
    description: "",
    price: "",
    duration: "",
    location: "",
  });

  const [selectedFile, setSelectedFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTripData({
      ...tripData,
      [name]: value,
    });
    // Clear any previous error when user starts typing
    if (error) setError("");
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    setError(""); // Clear any previous errors

    if (file) {
      // Validate file type
      if (!file.type.startsWith("image/")) {
        setError("Please select a valid image file (JPG, PNG, GIF, etc.)");
        return;
      }

      // Validate file size (5MB limit)
      if (file.size > 5 * 1024 * 1024) {
        setError("Image size should be less than 5MB");
        return;
      }

      setSelectedFile(file);

      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
    } else {
      setSelectedFile(null);
      setImagePreview(null);
    }
  };

  const resetForm = () => {
    setTripData({
      title: "",
      description: "",
      price: "",
      duration: "",
      location: "",
    });
    setSelectedFile(null);
    setImagePreview(null);
    setError("");
    setSuccess("");

    // Reset file input
    const fileInput = document.querySelector('input[type="file"]');
    if (fileInput) fileInput.value = "";
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError("");
    setSuccess("");

    // Validation
    if (!selectedFile) {
      setError("Please select an image for the trip");
      setIsLoading(false);
      return;
    }

    const formData = new FormData();
    formData.append("title", tripData.title.trim());
    formData.append("description", tripData.description.trim());
    formData.append("price", tripData.price);
    formData.append("duration", tripData.duration.trim());
    formData.append("location", tripData.location.trim());
    formData.append("image", selectedFile);

    try {
      const response = await fetch("/api/trips", {
        method: "POST",
        body: formData,
      });

      const result = await response.json();

      if (response.ok) {
        setSuccess("Trip created successfully!");
        resetForm();

        // Optional: Redirect after success
        // setTimeout(() => {
        //   window.location.href = '/admin/trips';
        // }, 2000);
      } else {
        setError(result.message || "Failed to create trip");
      }
    } catch (error) {
      console.error("Error creating trip:", error);
      setError("Network error. Please check your connection and try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="add-trip-container">
      <div className="add-trip-header">
        <h2>Add New Trip</h2>
        <p>Create a new travel package for customers</p>
      </div>

      {/* Success Message */}
      {success && (
        <div className="alert alert-success">
          <span className="alert-icon">✓</span>
          {success}
        </div>
      )}

      {/* Error Message */}
      {error && (
        <div className="alert alert-error">
          <span className="alert-icon">⚠</span>
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit} className="trip-form">
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="title">Trip Title *</label>
            <input
              type="text"
              id="title"
              name="title"
              value={tripData.title}
              onChange={handleInputChange}
              placeholder="e.g., 7 Days in Paris"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="location">Location *</label>
            <input
              type="text"
              id="location"
              name="location"
              value={tripData.location}
              onChange={handleInputChange}
              placeholder="e.g., Paris, France"
              required
            />
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="description">Description *</label>
          <textarea
            id="description"
            name="description"
            value={tripData.description}
            onChange={handleInputChange}
            placeholder="Describe the trip highlights, activities, and what makes it special..."
            rows="4"
            required
          />
          <small className="char-count">
            {tripData.description.length}/500 characters
          </small>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label htmlFor="price">Price (USD) *</label>
            <input
              type="number"
              id="price"
              name="price"
              value={tripData.price}
              onChange={handleInputChange}
              placeholder="999"
              min="0"
              step="0.01"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="duration">Duration *</label>
            <input
              type="text"
              id="duration"
              name="duration"
              value={tripData.duration}
              onChange={handleInputChange}
              placeholder="e.g., 7 days, 2 weeks"
              required
            />
          </div>
        </div>

        {/* File Upload Section */}
        <div className="form-group">
          <label htmlFor="image">Trip Image *</label>
          <div className="file-upload-area">
            <input
              type="file"
              id="image"
              accept="image/*"
              onChange={handleFileSelect}
              className="file-input"
              required
            />
            <div className="file-upload-text">
              <span className="upload-icon">📷</span>
              <p>
                {selectedFile
                  ? selectedFile.name
                  : "Click to upload an image or drag and drop"}
              </p>
              <small>PNG, JPG, GIF up to 5MB</small>
            </div>
          </div>
        </div>

        {/* Image Preview */}
        {imagePreview && (
          <div className="image-preview-section">
            <label>Image Preview:</label>
            <div className="image-preview">
              <img
                src={imagePreview}
                alt="Trip preview"
                className="preview-image"
              />
              <button
                type="button"
                className="remove-image-btn"
                onClick={() => {
                  setSelectedFile(null);
                  setImagePreview(null);
                  document.querySelector('input[type="file"]').value = "";
                }}
              >
                ✕ Remove
              </button>
            </div>
          </div>
        )}

        <div className="form-actions">
          <button
            type="button"
            className="btn btn-secondary"
            onClick={resetForm}
            disabled={isLoading}
          >
            Reset Form
          </button>

          <button
            type="submit"
            className="btn btn-primary"
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <span className="spinner"></span>
                Creating Trip...
              </>
            ) : (
              "Create Trip"
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddTrip;
